import {Component} from 'react';
import Room from './Room';
export default class Rooms extends Component{
    render(){

        const rooms = [{subj:'cn101',name:'jack'},{subj:'sf210',name:'paul'},{subj:'sf220',name:'piya'}]
        return(
            <section>
            <div className="row" style={{maxWidth:'70%'}}>

            {rooms.map(room=>{
                return(
                    
                    <Room
                    subj = {room.subj}
                    name={room.name}
                    >
                    </Room>
                )
            })}
            </div>
        </section>
    )
}
}
